import React, { useState } from "react";

const styles = {
  previewContainer: {
      marginTop: "20px",
      display: "flex",
      flexWrap: "wrap",
      justifyContent: "center",
    },
    // Add new styles for the preview image in the list
    previewListItem: {
      listStyle: "none",
      margin: "10px",
      textAlign: "center",
      cursor: "pointer", // Add cursor pointer to indicate it's clickable
    },
    // Add new style for the larger preview image on click
    previewItemLarge: {
      maxWidth: "300px", // You can adjust the width and height as needed
      borderRadius: "5px",
      boxShadow: "0 2px 5px rgba(0, 0, 0, 0.2)",
    },
};

const ImagePreview = ({ selectedFiles }) => {
  const [selectedImageIndex, setSelectedImageIndex] = useState(-1); // State to track the index of the selected image

  const handleImageClick = (index) => {
    setSelectedImageIndex(index); // Update the selectedImageIndex state when an image is clicked
  };

  return (
    <div>
      <ul id="previewContainer" style={styles.previewContainer}>
        {/* Render the images in horizontal list */}
        {selectedFiles.map((file, index) => (
          <li
            key={index}
            style={{
              ...styles.previewListItem,
              border: index === selectedImageIndex ? "2px solid #4CAF50" : "none", // Add border to indicate selected image
              ...(index === selectedImageIndex && styles.previewItemLarge), // Apply larger size to the selected image
            }}
            onClick={() => handleImageClick(index)} // Handle click on the image to make it larger
          >
            <img
              src={URL.createObjectURL(file)}
              alt={file.name}
              style={{ ...styles.previewItemImg, ...(index === selectedImageIndex && styles.previewItemLarge) }} // Apply larger size to the selected image
            />
            <div>{file.name}</div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ImagePreview;
