import React, { useState } from "react";
import axios from "axios";
import { Container, TextField, Button, Typography, Box } from "@mui/material";
import ImagePreview from './ImagePreview.js';
import { makeStyles } from "@mui/styles";

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor: theme.palette.background.paper,
    borderRadius: theme.shape.borderRadius,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(4),
    marginBottom: theme.spacing(4),
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
    backgroundColor: theme.palette.secondary.main,
  },
}));

const UploadForm = ({ onFileSelect, onSubmit, userName, setUserName, userEmail, setUserEmail }) => {
  const classes = useStyles();

  return (
    <form className={classes.form} onSubmit={onSubmit}>
      <TextField
        variant="outlined"
        margin="normal"
        required
        fullWidth
        label="Name"
        value={userName}
        onChange={(e) => setUserName(e.target.value)}
      />
      <TextField
        variant="outlined"
        margin="normal"
        required
        fullWidth
        label="Email"
        type="email"
        value={userEmail}
        onChange={(e) => setUserEmail(e.target.value)}
      />
      <Button
        variant="contained"
        component="label"
        fullWidth
      >
        Select Photos
        <input
          type="file"
          accept="image/png, image/jpeg, image/jpg"
          multiple
          onChange={onFileSelect}
          hidden
        />
      </Button>
      <Button
        type="submit"
        fullWidth
        variant="contained"
        color="primary"
        className={classes.submit}
      >
        Upload
      </Button>
    </form>
  );
};

const UploadPhotos = () => {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [userName, setUserName] = useState("");
  const [userEmail, setUserEmail] = useState("");

  const handleFileSelect = (event) => {
    // ... your existing code ...
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    // Prepare the data to send to the backend
    const formData = new FormData();
    selectedFiles.forEach((file) => {
      formData.append("photos", file);
    });

    try {
      // Make a POST request to the backend server to upload files
      await axios.post("https://your-azure-function-url", formData);

      // Reset the form after successful upload
      setSelectedFiles([]);
    } catch (error) {
      console.error("Error uploading files:", error);
      // Handle any error that occurred during the upload
    }
  };

  return (
    <Container component="main" maxWidth="xs">
      <UploadForm
        onFileSelect={handleFileSelect}
        onSubmit={handleSubmit}
        userName={userName}
        setUserName={setUserName}
        userEmail={userEmail}
        setUserEmail={setUserEmail}
      />
      <ImagePreview selectedFiles={selectedFiles} />
    </Container>
  );
};

export default UploadPhotos;
