import React, { useState } from "react";

const MAX_IMAGE_SIZE_MB = 7;
const MAX_IMAGE_SIZE_BYTES = MAX_IMAGE_SIZE_MB * 1024 * 1024;

const styles = {
  uploadContainer: {
    maxWidth: "600px",
    margin: "50px auto",
    backgroundColor: "rgba(255, 255, 255, 0.9)",
    borderRadius: "10px",
    padding: "20px",
    boxShadow: "0 2px 5px rgba(0, 0, 0, 0.2)",
  },
  h1: {
    textAlign: "center",
    marginBottom: "30px",
    color: "#333",
  },
  form: {
    marginBottom: "20px",
  },
  formGroup: {
    marginBottom: "20px",
  },
  formLabel: {
    display: "block",
    marginBottom: "5px",
    color: "#333",
  },
  formInput: {
    width: "100%",
    padding: "10px",
    fontSize: "16px",
    border: "1px solid #ccc",
    borderRadius: "5px",
  },
  uploadBtn: {
    backgroundColor: "#4CAF50",
    color: "#fff",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    width: "100%",
    fontSize: "16px",
  },
  previewContainer: {
    marginTop: "20px",
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
  },
  previewItem: {
    margin: "10px",
    textAlign: "center",
  },
  previewListItem: {
    listStyle: "none",
    margin: "5px",
    textAlign: "center",
  },
};

const UploadPhotos = () => {
  const [selectedFiles, setSelectedFiles] = useState([]);

  const handleFileSelect = (event) => {
    const previewContainer = document.getElementById("previewContainer");
    previewContainer.innerHTML = ""; // Clear previous previews
    const files = event.target.files;
    const newSelectedFiles = [...selectedFiles];
    for (const file of files) {
      if (!newSelectedFiles.includes(file.name)) {
        newSelectedFiles.push(file.name);
        if (file.type === "image/png" || file.type === "image/jpeg" || file.type === "image/jpg") {
          if (file.size <= MAX_IMAGE_SIZE_BYTES) {
            const previewItem = document.createElement("div");
            previewItem.classList.add("preview-item");
            if (file.type.startsWith("image/")) {
              const img = document.createElement("img");
              img.src = URL.createObjectURL(file);
              img.style.cssText = styles.previewItemImg;
              previewItem.appendChild(img);
            } else if (file.type.startsWith("video/")) {
              // Add video preview if needed
            }
            previewContainer.appendChild(previewItem);
          } else {
            alert(
              "Image '" + file.name + "' is too large. Max size allowed: " + MAX_IMAGE_SIZE_MB + " MB."
            );
          }
        } else {
          alert(
            "Image '" +
              file.name +
              "' has an unsupported file type. Only PNG, JPEG, and JPG are allowed."
          );
        }
      } else {
        alert("Image '" + file.name + "' is already selected and will not be uploaded again.");
      }
    }
    setSelectedFiles(newSelectedFiles);
  };

  return (
    <div style={styles.uploadContainer}>
      <h1 style={styles.h1}>Upload Photos</h1>
      <form style={styles.form}>
        {/* Input field and button remain the same... */}
      </form>
      <ul id="previewContainer" style={styles.previewContainer}>
        {/* Render the images in list items */}
        {selectedFiles.map((file) => (
          <li key={file} style={styles.previewListItem}>
            <img src={URL.createObjectURL(file)} alt={file.name} style={styles.previewItemImg} />
            <div>{file.name}</div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UploadPhotos;
