import React, { useState } from "react";
import UploadPhotos from "./UploadPhotos"; // Import the UploadPhotos component
import ImagePreview from './ImagePreview.js';

import backgroundPhoto from "./images/background.jpg"; // Import the background photo

const App = () => {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const appStyles = {
    backgroundImage: `url(${backgroundPhoto})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    minHeight: "50vh", // Set a minimum height to ensure the background covers the entire viewport
  };
  return (
    <div style={appStyles}>
      <UploadPhotos selectedFiles={selectedFiles} setSelectedFiles={setSelectedFiles} />
      <ImagePreview selectedFiles={selectedFiles} />
    </div>
  );
};

export default App;
