import React, { useState } from "react";

const MAX_IMAGE_SIZE_MB = 7;
const MAX_IMAGE_SIZE_BYTES = MAX_IMAGE_SIZE_MB * 1024 * 1024;
const MIN_IMAGE_WIDTH = 200;

const styles = {
  uploadContainer: {
    maxWidth: "600px",
    margin: "50px auto",
    backgroundColor: "rgba(255, 255, 255, 0.9)",
    borderRadius: "10px",
    padding: "20px",
    boxShadow: "0 2px 5px rgba(0, 0, 0, 0.2)",
  },
  h1: {
    textAlign: "center",
    marginBottom: "30px",
    color: "#333",
  },
  form: {
    marginBottom: "20px",
  },
  formGroup: {
    marginBottom: "20px",
  },
  formLabel: {
    display: "block",
    marginBottom: "5px",
    color: "#333",
  },
  formInput: {
    width: "100%",
    padding: "10px",
    fontSize: "16px",
    border: "1px solid #ccc",
    borderRadius: "5px",
  },
  uploadBtn: {
    backgroundColor: "#4CAF50",
    color: "#fff",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    width: "100%",
    fontSize: "16px",
  },
  previewContainer: {
    marginTop: "20px",
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
  },
  previewItem: {
    margin: "10px",
    textAlign: "center",
  },
  previewItemImg: {
    maxWidth: "150px",
    borderRadius: "5px",
    boxShadow: "0 2px 5px rgba(0, 0, 0, 0.2)",
  },
  previewListItem: {
    listStyle: "none",
    margin: "5px",
    textAlign: "center",
  },
};

const UploadPhotos = () => {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [userName, setUserName] = useState("");
  const [userEmail, setUserEmail] = useState("");

  const handleFileSelect = (event) => {
    const files = event.target.files;
    const newSelectedFiles = [];
    for (const file of files) {
      if (!selectedFiles.some((selectedFile) => selectedFile.name === file.name)) {
        newSelectedFiles.push(file);
        if (file.type === "image/png" || file.type === "image/jpeg" || file.type === "image/jpg") {
          if (file.size <= MAX_IMAGE_SIZE_BYTES) {
            // Image width validation
            const img = new Image();
            img.src = URL.createObjectURL(file);
            img.onload = function () {
              if (img.width >= MIN_IMAGE_WIDTH) {
                setSelectedFiles((prevSelectedFiles) => [...prevSelectedFiles, file]);
              } else {
                alert(
                  `Image '${file.name}' must have a width of at least ${MIN_IMAGE_WIDTH} pixels.`
                );
              }
            };
          } else {
            alert(
              "Image '" + file.name + "' is too large. Max size allowed: " + MAX_IMAGE_SIZE_MB + " MB."
            );
          }
        } else {
          alert(
            "Image '" +
              file.name +
              "' has an unsupported file type. Only PNG, JPEG, and JPG are allowed."
          );
        }
      } else {
        alert("Image '" + file.name + "' is already selected and will not be uploaded again.");
      }
    }
  };

  const handleRemoveImage = (index) => {
    setSelectedFiles((prevSelectedFiles) => {
      const updatedFiles = [...prevSelectedFiles];
      updatedFiles.splice(index, 1);
      return updatedFiles;
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Here, you can handle the submission of the form, including the selectedFiles array,
    // userName, and userEmail. For simplicity, we'll just log the data.
    console.log("Name:", userName);
    console.log("Email:", userEmail);
    console.log("Selected Files:", selectedFiles);
    // You can send this data to your server for further processing.
    // Reset the form after submission:
    setSelectedFiles([]);
    setUserName("");
    setUserEmail("");
  };


  return (
    <div style={styles.uploadContainer}>
      <h1 style={styles.h1}>Upload Photos</h1>
      <form style={styles.form} onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label style={styles.formLabel}>Name:</label>
          <input
            style={styles.formInput}
            type="text"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
          />
        </div>
        <div style={styles.formGroup}>
          <label style={styles.formLabel}>Email:</label>
          <input
            style={styles.formInput}
            type="email"
            value={userEmail}
            onChange={(e) => setUserEmail(e.target.value)}
          />
        </div>
        <div style={styles.formGroup}>
          <label style={styles.formLabel}>Select Photos:</label>
          <input
            style={styles.formInput}
            type="file"
            accept="image/png, image/jpeg, image/jpg"
            multiple
            onChange={handleFileSelect}
          />
        </div>
        <button style={styles.uploadBtn} type="submit">
          Upload
        </button>
      </form>
      <ul id="previewContainer" style={styles.previewContainer}>
        {/* Render the images in list items */}
        {selectedFiles.map((file) => (
          <li key={file.name} style={styles.previewListItem}>
            <img src={URL.createObjectURL(file)} alt={file.name} style={styles.previewItemImg} />
            <div>{file.name}</div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UploadPhotos;
